INSERT INTO `products` (`product_id`, `genre_id`, `product_name`, `price`, `stock`, `description`, `image_url`, `created_at`, `updated_at`) VALUES
(1, 1, 'Abbey Road - The Beatles', 29.99, 50, 'Album huyền thoại của The Beatles được phát hành năm 1969. Bao gồm các ca khúc nổi tiếng như \"Come Together\", \"Something\", \"Here Comes the Sun\". Đây là album cuối cùng được thu âm bởi ban nhạc.', 'https://upload.wikimedia.org/wikipedia/en/4/42/Beatles_-_Abbey_Road.jpg', '2025-05-23 06:59:34', '2025-05-25 07:59:37'),
(2, 2, '21 - Adele', 24.99, 45, 'Album phòng thu thứ hai của Adele, phát hành năm 2011. Album bao gồm các hit như \"Rolling in the Deep\", \"Someone Like You\", \"Set Fire to the Rain\". Đã giành được nhiều giải thưởng Grammy.', 'https://upload.wikimedia.org/wikipedia/en/1/1b/Adele_-_21.png', '2025-05-23 06:59:34', '2025-05-25 07:59:37'),
(3, 2, 'BORN PINK - BLACKPINK', 19.99, 35, 'Born Pink là album phòng thu thứ hai của nhóm nhạc nữ Hàn Quốc Blackpink, được phát hành vào ngày 16 tháng 9 năm 2022, bởi YG Entertainment và Interscope Records', 'https://upload.wikimedia.org/wikipedia/vi/e/e7/Born_Pink_Digital.jpeg', '2025-05-21 06:59:34', '2025-05-25 09:12:03'),
(4, 1, 'Sgt. Pepper\'s Lonely Hearts Club Band - The Beatles', 32.99, 30, 'Album thứ tám của The Beatles, được phát hành năm 1967. Được coi là một trong những album có ảnh hưởng nhất trong lịch sử âm nhạc rock.', 'https://upload.wikimedia.org/wikipedia/en/5/50/Sgt._Pepper%27s_Lonely_Hearts_Club_Band.jpg', '2025-05-21 06:59:34', '2025-05-26 02:09:59'),
(5, 2, '25 - Adele', 26.99, 40, 'Album phòng thu thứ ba của Adele, phát hành năm 2015. Bao gồm hit \"Hello\" và nhiều bản ballad cảm động khác.', 'https://upload.wikimedia.org/wikipedia/en/9/96/Adele_-_25_%28Official_Album_Cover%29.png', '2025-05-11 06:59:34', '2025-05-26 02:09:59'),
(6, 3, 'Thriller - Michael Jackson', 39.99, 25, 'Album solo thứ sáu của Michael Jackson, phát hành năm 1982. Là album bán chạy nhất mọi thời đại với hơn 66 triệu bản trên toàn thế giới.', 'https://upload.wikimedia.org/wikipedia/en/5/55/Michael_Jackson_-_Thriller.png', '2025-05-10 06:59:34', '2025-05-26 02:09:59'),
(7, 4, 'Kind of Blue - Miles Davis', 34.99, 20, 'Album jazz kinh điển được phát hành năm 1959. Được coi là một trong những album jazz có ảnh hưởng nhất mọi thời đại.', 'https://upload.wikimedia.org/wikipedia/en/9/9c/MilesDavisKindofBlue.jpg', '2025-05-08 06:59:34', '2025-05-26 02:09:59'),
(8, 5, 'The Dark Side of the Moon - Pink Floyd', 37.99, 15, 'Album phòng thu thứ tám của Pink Floyd, phát hành năm 1973. Khám phá các chủ đề xung đột, tham lam, thời gian và bệnh tâm thần.', 'https://upload.wikimedia.org/wikipedia/en/3/3b/Dark_Side_of_the_Moon.png', '2025-05-05 06:59:34', '2025-05-26 02:09:59'),
(9, 6, 'Nevermind - Nirvana', 28.99, 22, 'Album phòng thu thứ hai của Nirvana, phát hành năm 1991. Bao gồm hit \"Smells Like Teen Spirit\" đã định hình lại cảnh âm nhạc những năm 90.', 'https://upload.wikimedia.org/wikipedia/en/b/b7/NirvanaNevermindalbumcover.jpg', '2025-05-03 06:59:34', '2025-05-26 02:09:59'),
(10, 7, 'Sơn Tùng M-TP Collection', 15.99, 55, 'Tuyển tập những hit hay nhất của Sơn Tùng M-TP bao gồm \"Chúng ta không thuộc về nhau\", \"Hãy trao cho anh\", \"Muộn rồi mà sao còn\"', 'https://example.com/sontung.jpg', '2025-05-01 06:59:34', '2025-05-26 02:09:59');
INSERT INTO `accessories` (`accessory_id`, `accessory_name`, `description`, `price`, `stock`, `image_url`, `created_at`, `updated_at`) VALUES
(1, 'Tai nghe Sony WH-1000XM4', 'Tai nghe không dây chống ồn cao cấp với chất lượng âm thanh tuyệt vời và thời lượng pin lên đến 30 giờ', 349.99, 25, 'https://images.unsplash.com/photo-1546435770-a3e426bf472b?w=400&h=400&fit=crop', '2025-05-26 08:11:52', '2025-05-26 09:23:16'),
(2, 'Loa Marshall Acton II', 'Loa Bluetooth phong cách vintage với âm thanh Marshall đặc trưng, thiết kế nhỏ gọn phù hợp mọi không gian.', 279.99, 15, 'https://images.unsplash.com/photo-1608043152269-423dbba4e7e1?w=400&h=400&fit=crop', '2025-05-26 08:11:52', '2025-05-26 08:11:52'),
(3, 'Đầu đĩa Audio-Technica AT-LP60X', 'Đầu đĩa than tự động đầy đủ tính năng với chất lượng âm thanh analog ấm áp và thiết kế hiện đại.', 149.99, 12, 'https://images.unsplash.com/photo-1470225620780-dba8ba36b745?w=400&h=400&fit=crop', '2025-05-26 08:11:52', '2025-05-26 08:11:52'),
(4, 'Microphone Blue Yeti', 'Microphone USB chuyên nghiệp với 4 chế độ pickup pattern, hoàn hảo cho recording và streaming.', 99.99, 20, 'https://images.unsplash.com/photo-1590602847861-f357a9332bbc?w=400&h=400&fit=crop', '2025-05-26 08:11:52', '2025-05-26 08:11:52'),
(5, 'Cable XLR Mogami Gold', 'Cáp XLR chất lượng cao từ Mogami với độ bền và chất lượng truyền tín hiệu tuyệt vời.', 45.99, 50, 'https://images.unsplash.com/photo-1558618047-3c8c76ca7d13?w=400&h=400&fit=crop', '2025-05-26 08:11:52', '2025-05-26 08:11:52'),
(6, 'Kệ đĩa nhạc gỗ tre', 'Kệ để đĩa nhạc làm từ gỗ tre tự nhiên, thiết kế đơn giản và thân thiện với môi trường.', 89.99, 18, 'https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=400&h=400&fit=crop', '2025-05-26 08:11:52', '2025-05-26 08:11:52'),
(7, 'Tai nghe Sennheiser HD650', 'Tai nghe over-ear cao cấp với driver dynamic mở, mang đến trải nghiệm âm thanh audiophile chuyên nghiệp.', 499.99, 8, 'https://example.com/sennheiser.jpg', '2025-05-25 08:11:52', '2025-05-26 08:11:52'),
(8, 'Ampli đèn Tube Amp Classic', 'Ampli đèn vintage mang lại âm thanh ấm áp, phù hợp với những người yêu thích âm thanh analog.', 899.99, 5, 'https://example.com/tubeamp.jpg', '2025-05-24 08:11:52', '2025-05-26 08:11:52'),
(9, 'Bộ làm sạch đĩa nhạc', 'Bộ dụng cụ chuyên dụng để vệ sinh và bảo quản đĩa vinyl, giúp duy trì chất lượng âm thanh tốt nhất.', 39.99, 30, 'https://example.com/cleaning-kit.jpg', '2025-05-23 08:11:52', '2025-05-26 08:11:52'),
(10, 'Đế chống rung isolation pads', 'Bộ đế chống rung chuyên dụng cho đầu đĩa và loa, giảm thiểu tác động của rung động bên ngoài.', 24.99, 40, 'https://example.com/isolation-pads.jpg', '2025-05-22 08:11:52', '2025-05-26 08:11:52'); 